import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    sender: '',
    receiver: '',
    amount: '',
    subject: '',
    actionLevel: '',
    dimension: '',
    fluctuation: '',
    organismName: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/send', formData)
      .then(response => {
        console.log('Success:', response);
      })
      .catch(error => {
        console.error('Error:', error);
      });
  };

  return (
    <div className="App">
      <h1>Send Love Currency</h1>
      <form onSubmit={handleSubmit}>
        <label>Sender:</label>
        <input type="text" name="sender" value={formData.sender} onChange={handleChange} required />
        <br />

        <label>Receiver:</label>
        <input type="text" name="receiver" value={formData.receiver} onChange={handleChange} required />
        <br />

        <label>Amount:</label>
        <input type="number" name="amount" value={formData.amount} onChange={handleChange} required />
        <br />

        <label>Subject:</label>
        <input type="text" name="subject" value={formData.subject} onChange={handleChange} />
        <br />

        <label>Action Level:</label>
        <input type="text" name="actionLevel" value={formData.actionLevel} onChange={handleChange} />
        <br />

        <label>Dimension:</label>
        <input type="text" name="dimension" value={formData.dimension} onChange={handleChange} />
        <br />

        <label>Fluctuation:</label>
        <input type="text" name="fluctuation" value={formData.fluctuation} onChange={handleChange} />
        <br />

        <label>Organism Name:</label>
        <input type="text" name="organismName" value={formData.organismName} onChange={handleChange} />
        <br />

        <button type="submit">Send Love Currency</button>
      </form>
    </div>
  );
}

export default App;
