import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [sender, setSender] = useState('');
    const [receiver, setReceiver] = useState('');
    const [amount, setAmount] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('https://your-backend-url.com/create_transaction', {
                sender,
                receiver,
                amount,
                timestamp: new Date().toISOString(),
                transaction_id: Math.random().toString(36).substr(2, 9),
                verifiable_credential: 'example_credential'
            });
            setMessage('Transaction successful!');
        } catch (error) {
            setMessage('Transaction failed.');
        }
    };

    return (
        <div>
            <h1>Love Currency Exchange</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Sender:</label>
                    <input 
                        type="text" 
                        value={sender} 
                        onChange={(e) => setSender(e.target.value)} 
                    />
                </div>
                <div>
                    <label>Receiver:</label>
                    <input 
                        type="text" 
                        value={receiver} 
                        onChange={(e) => setReceiver(e.target.value)} 
                    />
                </div>
                <div>
                    <label>Amount:</label>
                    <input 
                        type="number" 
                        value={amount} 
                        onChange={(e) => setAmount(e.target.value)} 
                    />
                </div>
                <button type="submit">Send Love Currency</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default App;
